sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var whtNeeded = g_form.getValue('u_choice_3');
	if(newValue == 'HR'){
		g_form.addOption('u_choice_3','HR 1','Human Resource 1');
		g_form.addOption('u_choice_3','HR 2','Human Resource 2');
		g_form.addOption('u_choice_3','other','other');
	} 
	if(newValue == 'Facility'){
		g_form.addOption('u_choice_3','Facilities 1','Facilities 1');
		g_form.addOption('u_choice_3','Facilities 2','Facilities 2');
		g_form.addOption('u_choice_3','other','other');
	} 
	if(newValue == 'Legal') {
		g_form.addOption('u_choice_3','Legal 1','Legal 1');
		g_form.addOption('u_choice_3','	Legal 2','Legal 2');
		g_form.addOption('u_choice_3','other','other');
	}
	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_choice_3',whtNeeded);
	}
}
